",n(s.figure,{"data-rehype-pretty-code-figure":"",children:n(s.pre,{style:{backgroundColor:"#282c34",color:"#abb2bf"},tabIndex:"0",rawcontent:"// pages/api/perplexity.js (或 app/api/perplexity/route.js)
export default async function handler(req, res) {
    if (req.method !== 'POST') {
        return res.status(405).json({ error: 'Method not allowed' });
    }

    const { question } = req.body;

    try {
        const response = await fetch('https://api.perplexity.ai/chat/completions', {
            method: 'POST',
            headers: {
                Authorization: `Bearer ${process.env.PERPLEXITY_API_KEY}`,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                model: 'sonar',
                messages: [
                    {
                        role: 'system',
                        content: '請用繁體中文回答，並在回答中包含相關的來源連結',
                    },
                    { role: 'user', content: question },
                ],
                temperature: 0.3,
                max_tokens: 1000,
            }),
        });

        const data = await response.json();
        const answer = data.choices[0].message.content;

        res.status(200).json({ answer });
    } catch (error) {
        console.error('Perplexity API 錯誤:', error);
        res.status(500).json({ error: '搜尋服務暫時無法使用' });
    }
