import { useCreateUser } from '@/hooks/useCreateUser';

export default function CreateUserForm() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const createUserMutation = useCreateUser();

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        createUserMutation.mutate({ name, email });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="姓名"
                required
            />
            <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
                required
            />
            <button
                type="submit"
                disabled={createUserMutation.isPending}
            >
                {createUserMutation.isPending ? '建立中...' : '建立用戶'}
            </button>
        </form>
    );
}","data-language":"typescript","data-theme":"one-dark-pro",children:n(s.code,{"data-language":"typescript","data-theme":"one-dark-pro",style:{display:"grid"},children:[n(s.span,{"data-line":"",children:[l(s.span,{style:{color:"#C678DD"},children:"import"}),l(s.span,{style:{color:"#ABB2BF"},children:" { "}),l(s.span,{style:{color:"#E06C75"},children:"useState"}),l(s.span,{style:{color:"#ABB2BF"},children:" } "}),l(s.span,{style:{color:"#C678DD"},children:"from"}),l(s.span,{style:{color:"#98C379"},children:" 'react'"}),l(s.span,{style:{color:"#ABB2BF"},children:";"})]}),"
